package ch05.function;

public class Function2 {

	public static void main(String[] args) {
		
		String result = numbers(2); 
		System.out.println(result);
	}
	
	public static String numbers(int a) {
		
		if (a % 2 == 0) {
			return "짝수";
		} else {
			return "홀수";                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
		}
	
	} 
}
